import React from "react";
import "./App.css";
import mondaySdk from "monday-sdk-js";
const monday = mondaySdk();

class App extends React.Component {
  constructor(props) {
    super(props);

    // Default state
    this.state = {
      settings: {},
      name: "",
    };
  }

  componentDidMount() {
    monday.listen("settings", res => {
      this.setState({ settings: res.data });
      console.log(settings);
    });
  }

  render() {
    return <div className="App" style={{background: (this.state.settings.background)}}>Hello, monday Apps!</div>;
  }
}

export default App;
