import React from "react";
import PieChart, {
  Legend,
  Series,
  Tooltip,
  Format,
  Label,
  Connector,
  Export,
} from "devextreme-react/pie-chart";

const populationByRegions = [
  {
    region: "Asia",
    val: 4119626293,
  },
  {
    region: "Africa",
    val: 1012956064,
  },
  {
    region: "Northern America",
    val: 344124520,
  },
  {
    region: "Latin America and the Caribbean",
    val: 590946440,
  },
  {
    region: "Europe",
    val: 727082222,
  },
  {
    region: "Oceania",
    val: 35104756,
  },
];

class App extends React.Component {
  render() {
    const map = {};
    this.props.data.items.forEach(function (arrayItem) {
      var productiveHour = arrayItem.column_values.find((obj) => {
        return obj.title === "Most Productive Hours";
      }).text;

      map[productiveHour] = map[productiveHour] + 1 || 1;
    });

    const chartData = [];
    for (const key in map) {
      chartData.push({ productiveHour: key, count: map[key] });
    }

    return (
      <PieChart
        id="pie"
        type="doughnut"
        title="Most Productive Hours - Donut Chart"
        palette="Soft Pastel"
        dataSource={chartData}
      >
        <Series argumentField="productiveHour">
          <Label visible={true} format="millions">
            <Connector visible={true} />
          </Label>
        </Series>
        <Export enabled={true} />
        <Legend
          margin={0}
          horizontalAlignment="right"
          verticalAlignment="top"
        />
        <Tooltip enabled={true} customizeTooltip={this.customizeTooltip}>
          <Format type="millions" />
        </Tooltip>
      </PieChart>
    );
  }

  customizeTooltip(arg) {
    return {
      text: `${arg.valueText} - ${(arg.percent * 100).toFixed(2)}%`,
    };
  }
}

export default App;
