import * as React from "react";
import Paper from "@material-ui/core/Paper";
import {
  Chart,
  BarSeries,
  Title,
  ArgumentAxis,
  ValueAxis,
} from "@devexpress/dx-react-chart-material-ui";

import { Animation } from "@devexpress/dx-react-chart";

const data = [
  { year: "1950", population: 2.525 },
  { year: "1960", population: 3.018 },
  { year: "1970", population: 3.682 },
  { year: "1980", population: 4.44 },
  { year: "1990", population: 5.31 },
  { year: "2000", population: 6.127 },
  { year: "2010", population: 6.93 },
];

export default class Demo extends React.PureComponent {
  render() {
    const map = {};
    this.props.data.items.forEach(function (arrayItem) {
      var productiveHour = arrayItem.column_values.find((obj) => {
        return obj.title === "Most Productive Hours";
      }).text;

      map[productiveHour] = map[productiveHour] + 1 || 1;
    });

    const chartData = [];
    for (const key in map) {
      chartData.push({ productiveHour: key, count: map[key] });
    }

    return (
      <Paper>
        <Chart data={chartData}>
          <ArgumentAxis />
          <ValueAxis max={7} />

          <BarSeries valueField="count" argumentField="productiveHour" />
          <Title text="Most Productive Hours" />
          <Animation />
        </Chart>
      </Paper>
    );
  }
}
